.btn-77,
.btn-77 *,
.btn-77 :after,
.btn-77 :before,
.btn-77:after,
.btn-77:before {
  border: 0 solid;
  box-sizing: border-box;
}
.btn-77 {
  -webkit-tap-highlight-color: 
transparent;
  -webkit-appearance: button;
  background-color: 
#000;
  background-image: none;
  color: 
#fff;
  cursor: pointer;
  font-family: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont,
    Segoe UI, Roboto, Helvetica Neue, Arial, Noto Sans, sans-serif,
    Apple Color Emoji, Segoe UI Emoji, Segoe UI Symbol, Noto Color Emoji;
  font-size: 100%;
  line-height: 1.5;
  margin: 0;
  -webkit-mask-image: -webkit-radial-gradient(#000, #fff);
  padding: 0;
}
.btn-77:disabled {
  cursor: default;
}
.btn-77:-moz-focusring {
  outline: auto;
}
.btn-77 svg {
  display: block;
  vertical-align: middle;
}
.btn-77 [hidden] {
  display: none;
}
.btn-77 {
  -webkit-animation: pulse 2s infinite;
  animation: pulse 2s infinite;
  border: 1px solid;
  border-radius: 999px;
  box-shadow: 0 0 0 2em 
transparent;
  box-sizing: border-box;
  display: block;
  font-weight: 900;
  -webkit-mask-image: none;
  overflow: hidden;
  padding: 1.2rem 3rem;
  position: relative;
  text-transform: uppercase;
}
@-webkit-keyframes pulse {
  0% {
    box-shadow: 0 0 0 0 
#fff;
  }
}
@keyframes pulse {
  0% {
    box-shadow: 0 0 0 0 
#fff;
  }
}

.btn-37,
.btn-37 *,
.btn-37 :after,
.btn-37 :before,
.btn-37:after,
.btn-37:before {
  border: 0 solid;
  box-sizing: border-box;
}
.btn-37 {
  -webkit-tap-highlight-color: 
transparent;
  -webkit-appearance: button;
  background-color: 
#000;
  background-image: none;
  color: 
#fff;
  cursor: pointer;
  font-family: ui-sans-serif, system-ui, -apple-system, BlinkMacSystemFont,
    Segoe UI, Roboto, Helvetica Neue, Arial, Noto Sans, sans-serif,
    Apple Color Emoji, Segoe UI Emoji, Segoe UI Symbol, Noto Color Emoji;
  font-size: 100%;
  font-weight: 900;
  line-height: 1.5;
  margin: 0;
  -webkit-mask-image: -webkit-radial-gradient(#000, #fff);
  padding: 0;
}
.btn-37:disabled {
  cursor: default;
}
.btn-37:-moz-focusring {
  outline: auto;
}
.btn-37 svg {
  display: block;
  vertical-align: middle;
}
.btn-37 [hidden] {
  display: none;
}
.btn-37 {
  border: 1px solid;
  box-sizing: border-box;
  display: block;
  overflow: hidden;
  padding: 1.1rem 4rem;
  position: relative;
  text-transform: uppercase;
}
.btn-37 .new,
.btn-37 .old span {
  font-weight: 900;
  transition: transform 0.2s;
}
.btn-37 .new {
  display: block;
  transform: scale(0);
}
.btn-37:hover .new {
  transform: scale(1);
}
.btn-37 .old,
.btn-37 .old span {
  inset: 0;
  position: absolute;
}
.btn-37 .old span {
  background: 
#fff;
  color: 
#000;
  display: block;
  display: grid;
  place-items: center;
}
.btn-37 .old span:first-child {
  -webkit-clip-path: polygon(0 0, 51% 0, 51% 100%, 0 100%);
  clip-path: polygon(0 0, 51% 0, 51% 100%, 0 100%);
}
.btn-37 .old span:nth-child(2) {
  -webkit-clip-path: polygon(50% 0, 100% 0, 100% 100%, 50% 100%);
  clip-path: polygon(50% 0, 100% 0, 100% 100%, 50% 100%);
}
.btn-37:hover .old span:first-child {
  transform: translateY(-100%);
}
.btn-37:hover .old span:nth-child(2) {
  transform: translateY(100%);
}

.btn-neon {
  background-color: #000;
  border: none;
  color: #fff;
  font-size: 16px;
  padding: 10px 20px;
  cursor: pointer;
  box-shadow: 0 0 10px #0ff, 0 0 20px #0ff, 0 0 30px #0ff;
}
.btn-neon:hover {
  box-shadow: 0 0 20px #0ff, 0 0 30px #0ff, 0 0 40px #0ff;
}

.btn-gradient-border {
  background-color: #000;
  border: 2px solid transparent;
  border-image: linear-gradient(45deg, #f3ec78, #af4261) 1;
  color: #fff;
  padding: 10px 20px;
  cursor: pointer;
  font-size: 16px;
}
.btn-gradient-border:hover {
  background-color: #333;
}

.btn-shadow {
  background-color: #6200ea;
  border: none;
  color: #fff;
  padding: 12px 24px;
  cursor: pointer;
  font-size: 18px;
  box-shadow: 0 5px 15px rgba(0, 0, 0, 0.3);
  transition: box-shadow 0.3s ease-in-out;
}
.btn-shadow:hover {
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.5);
}

.btn-flat {
  background-color: #2ecc71;
  border: none;
  color: #fff;
  padding: 14px 28px;
  cursor: pointer;
  font-size: 16px;
  box-shadow: none;
}
.btn-flat:hover {
  background-color: #27ae60;
}

.btn-outline {
  background-color: transparent;
  border: 2px solid #3498db;
  color: #3498db;
  padding: 10px 20px;
  cursor: pointer;
  font-size: 16px;
  transition: background-color 0.3s, color 0.3s;
}
.btn-outline:hover {
  background-color: #3498db;
  color: #fff;
}

.btn-3d {
  background-color: #e74c3c;
  border: none;
  color: #fff;
  padding: 12px 24px;
  cursor: pointer;
  font-size: 16px;
  box-shadow: 0 6px #c0392b;
  transition: box-shadow 0.1s ease-in-out;
}
.btn-3d:active {
  box-shadow: 0 3px #c0392b;
  transform: translateY(3px);
}

.btn-ripple {
  background-color: #009688;
  border: none;
  color: #fff;
  padding: 14px 28px;
  cursor: pointer;
  font-size: 16px;
  overflow: hidden;
  position: relative;
}
.btn-ripple::after {
  content: '';
  display: block;
  position: absolute;
  width: 100%;
  height: 100%;
  top: 0;
  left: 0;
  pointer-events: none;
  background: rgba(255, 255, 255, 0.3);
  opacity: 0;
  transition: opacity 0.5s;
}
.btn-ripple:active::after {
  opacity: 1;
  transition: opacity 0.1s;
}

.input-rounded {
  border: 2px solid #3498db;
  border-radius: 25px;
  padding: 10px 20px;
  font-size: 16px;
  outline: none;
}
.input-rounded:focus {
  border-color: #2980b9;
}

.input-underline {
  border: none;
  background-color:transparent;
  border-bottom: 2px solid #3498db;
  padding: 10px 0;
  font-size: 16px;
  outline: none;
}
.input-underline:focus {
  border-color: #2980b9;
}

.input-shadow {
  border: 1px solid #ddd;
  padding: 10px 20px;
  font-size: 16px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  outline: none;
}
.input-shadow:focus {
  box-shadow: 0 4px 8px rgba(0, 0, 0, 0.2);
}

.input-transparent {
  border: 2px solid rgba(255, 255, 255, 0.5);
  background: rgba(255, 255, 255, 0.1);
  padding: 10px 20px;
  font-size: 16px;
  color: #fff;
  outline: none;
}
.input-transparent:focus {
  border-color: rgba(255, 255, 255, 0.8);
  background: rgba(255, 255, 255, 0.2);
}

.material-input {
  position: relative;
  margin-bottom: 20px;
}
.material-input input {
  border: none;
  border-bottom: 2px solid #ccc;
  padding: 10px 0;
  font-size: 16px;
  width: 100%;
  outline: none;
}
.material-input label {
  position: absolute;
  top: 10px;
  left: 0;
  font-size: 16px;
  color: #999;
  transition: 0.2s;
  pointer-events: none;
}
.material-input input:focus + label,
.material-input input:not(:placeholder-shown) + label {
  top: -20px;
  font-size: 12px;
  color: #3498db;
}
.material-input input:focus {
  border-color: #3498db;
}

.input-disabled {
  border: 1px solid #ddd;
  padding: 10px 20px;
  font-size: 16px;
  background-color: #f9f9f9;
  color: #aaa;
  cursor: not-allowed;
}

.search-input {
  position: relative;
  display: flex;
  align-items: center;
  border: 1px solid #ccc;
  padding: 10px;
}
.search-input .search-icon {
  margin-left: 10px;
  color: #999;
}
.search-input input {
  border: none;
  outline: none;
  font-size: 16px;
  flex: 1;
}
.search-input:focus-within {
  border-color: #3498db;
}

.switch {
  position: relative;
  display: inline-block;
  width: 60px;
  height: 34px;
}
.switch input {
  opacity: 0;
  width: 0;
  height: 0;
}
.slider {
  position: absolute;
  cursor: pointer;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background-color: #ccc;
  transition: .4s;
}
.slider:before {
  position: absolute;
  content: "";
  height: 26px;
  width: 26px;
  left: 4px;
  bottom: 4px;
  background-color: white;
  transition: .4s;
}
input:checked + .slider {
  background-color: #2196F3;
}
input:checked + .slider:before {
  transform: translateX(26px);
}
.slider.round {
  border-radius: 34px;
}
.slider.round:before {
  border-radius: 50%;
}

.fancy-checkbox {
  display: block;
  position: relative;
  padding-left: 35px;
  margin-bottom: 12px;
  cursor: pointer;
  font-size: 22px;
  user-select: none;
}
.fancy-checkbox input {
  position: absolute;
  opacity: 0;
  cursor: pointer;
}
.fancy-checkmark {
  position: absolute;
  top: 0;
  left: 0;
  height: 25px;
  width: 25px;
  background-color: #eee;
}
.fancy-checkbox:hover input ~ .fancy-checkmark {
  background-color: #ccc;
}
.fancy-checkbox input:checked ~ .fancy-checkmark {
  background-color: #2196F3;
}
.fancy-checkmark:after {
  content: "";
  position: absolute;
  display: none;
}
.fancy-checkbox input:checked ~ .fancy-checkmark:after {
  display: block;
}
.fancy-checkbox .fancy-checkmark:after {
  left: 9px;
  top: 5px;
  width: 5px;
  height: 10px;
  border: solid white;
  border-width: 0 3px 3px 0;
  transform: rotate(45deg);
}

.round-checkbox {
  display: block;
  position: relative;
  padding-left: 35px;
  margin-bottom: 12px;
  cursor: pointer;
  font-size: 22px;
  user-select: none;
}
.round-checkbox input {
  position: absolute;
  opacity: 0;
  cursor: pointer;
}
.round-checkmark {
  position: absolute;
  top: 0;
  left: 0;
  height: 25px;
  width: 25px;
  background-color: #eee;
  border-radius: 50%;
}
.round-checkbox:hover input ~ .round-checkmark {
  background-color: #ccc;
}
.round-checkbox input:checked ~ .round-checkmark {
  background-color: #2196F3;
}
.round-checkmark:after {
  content: "";
  position: absolute;
  display: none;
}
.round-checkbox input:checked ~ .round-checkmark:after {
  display: block;
}
.round-checkbox .round-checkmark:after {
  top: 9px;
  left: 9px;
  width: 8px;
  height: 8px;
  border-radius: 50%;
  background: white;
}
